mapper.select:
    
    select 
    T.FID, T.FNAME_L2, T.FNUMBER, T.CFPRICERANGE, T.FMODEL, T.CFBRAND, T.CFTYPE,
    T.CFCOLLECTIONID, T.CFINDEX, T.CFLENSSPHERE, T.CFLENSCYLINDER, T.CFTYPEINCLUDE,
    T.CFRX ,T.CFSUPPLIERID,T.FBARCODE
    from t_bd_material T 
    left join ct_rbd_brand BRAND on T.CFBRANDID = BRAND.FID
    left join ct_rbd_succession SUC on T.CFCOLLECTIONID = SUC.FID
    
	    	    	
		    	
		    	and T.FNAME_L2 like '%${name}%'
		    	
		    	
		    	and T.CFINDEX like '%${index}%'
		    	
		    	
		    	and T.FMODEL like '%${model}%'
		    	
		    	
		    	and T.CFLENSSPHERE = #{lenssphere}
		    	
		    	
		    	and T.CFLENSCYLINDER = #{lenscylinder}
		    	
		    	
		    	and T.CFBRANDID = #{brand, jdbcType=VARCHAR}
		    	
		    	
		    	and T.CFCOLLECTIONID = #{collectionID,jdbcType=VARCHAR}
		    	
		    	
		    	and T.CFTYPEINCLUDE like '%${typeInclude}%'
		    	
		    	
		    	and T.CFPRICERANGE >= #{priceRangeFrom}
		    	
		    	
		    	and T.CFPRICERANGE <= #{priceRangeEnd}
		    	
		    	
		    	and T.FBARCODE = #{barcode}
		    		    	
		    	
		    	and T.CFRX = #{rx}
		    		  
		    	
		    	and T.fnumber like '%${number}%'
		    		  	
	    
    
    
     ORDER BY ${orderByClause} ${orderBySort}
    
    
     LIMIT ${numStart},${numEnd}

mapper.insert:

insert into t_bd_material (FID, FDESCRIPTION_L1, FNUMBER, 
  FBARCODE, FHELPCODE, CFBRAND, 
  CFCOLOR, FNAME_L1, FNAME_L2, FNAME_L3,FCREATORID,FLASTUPDATEUSERID
  )
values (#{id,jdbcType=VARCHAR}, #{description,jdbcType=VARCHAR},#{number,jdbcType=VARCHAR}
,#{barcode,jdbcType=VARCHAR},#{helpCode,jdbcType=VARCHAR},#{brand,jdbcType=VARCHAR},
#{color,jdbcType=VARCHAR}  ,#{name,jdbcType=VARCHAR},#{name,jdbcType=VARCHAR},#{name,jdbcType=VARCHAR},'',''
  )

mapper.select:
    select 
    T.FID, T.FNAME_L2, T.FNUMBER, T.CFPRICERANGE, T.FMODEL, T.CFBRAND, T.CFTYPE,
    T.CFCOLLECTIONID, T.CFINDEX, T.CFLENSSPHERE, T.CFLENSCYLINDER, T.CFTYPEINCLUDE,
    T.CFRX ,T.CFSUPPLIERID,T.FBARCODE
    from t_bd_material T 
    left join ct_rbd_brand BRAND on T.CFBRANDID = BRAND.FID
    left join ct_rbd_succession SUC on T.CFCOLLECTIONID = SUC.FID
    
	    	    	
		    	
		    	and T.FNUMBER >= #{numberFrom}
		    		    	
		    	
		    	and T.FNUMBER <= #{numberTo}
		    		    	
	    
    
    
     ORDER BY ${orderByClause} ${orderBySort}
    
    
     LIMIT ${numStart},${numEnd}
